int char_to_number (char x)
{
	int y;
	
	switch(x)
	{
      case '0': y = 0; break;
      case '1': y = 1; break;
      case '2': y = 2; break;
      case '3': y = 3; break;
      case '4': y = 4; break;
      case '5': y = 5; break;
      case '6': y = 6; break;
      case '7': y = 7; break;
      case '8': y = 8; break;
			case '9': y = 9; break;
      default: y = 10; break;
  }
	return y;
}

void UART_INT_HANDLE(void)
{
    uint8_t bInChar[14];
		
		if(UART0->ISR.RDA_IF==1) 
		{
			DrvUART_Read(UART_PORT0,bInChar,8);
			if (ship_number == 9)
				ship_number = char_to_number(bInChar[4]);
			else
			{
				if(char_to_number(bInChar[4]) == 9)
					losser = 1;
				else
					my_turn = char_to_number(bInChar[4]);
			}
		}
}


